df = pd.DataFrame(data)

x = df[["Inversi√≥n en publicidad (USD)"]]
y = df[["Ventas (unidades)"]]
model = LinearRegression()
model.fit(x, y)

def calculate_prediction(investment):
    investment_df = pd.DataFrame([[investment]], columns=["Inversi√≥n en publicidad (USD)"])
    result = model.predict(investment_df)[0][0]
    return round(result, 2)

def generate_plot():
    plt.figure(figsize=(6, 4))
    plt.scatter(df["Inversi√≥n en publicidad (USD)"], df["Ventas (unidades)"], color='blue', label="Datos")
    plt.plot(df["Inversi√≥n en publicidad (USD)"], model.predict(x), color='red', linewidth=2, label="Regresi√≥n Lineal")
    plt.xlabel("Inversi√≥n en publicidad (USD)")
    plt.ylabel("Ventas (unidades)")
    plt.title("Regresi√≥n Lineal: Publicidad vs. Ventas")
    plt.legend()
    
    img = io.BytesIO()
    plt.savefig(img, format='png')
    img.seek(0)
    plot_url = base64.b64encode(img.getvalue()).decode()
    plt.close()
    return plot_url

